package unidad_02_Variables;

public class Ejercicio01_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x;
int y;
	x=144;
	y=999;
System.out.println("La variable \"x\" vale: "+x);
System.out.println("La variable \"y\" vale: "+y);
	System.out.println("La suma de las variables \"x\" e \"y\" es: "+(x+y));
	System.out.println("La resta de las variables \"x\" e \"y\" es: "+(y-x));
	System.out.println("La división de las variables \"x\" e \"y\" es: "+(y/x));
	System.out.println("La multiplicación de las variables \"x\" e \"y\" es: "+(x*y));
	}

}
